/** The state of a promise, reified */
export type PromiseState<T> = {
    status: 'pending';
} | {
    status: 'resolved';
    value: T;
} | {
    status: 'rejected';
    reason?: any;
};
/** A Promise and it's state */
export type TrackedPromise<T> = Promise<T> & PromiseState<T>;
export declare function track<T>(task: () => Promise<T>): () => TrackedPromise<T>;
